{ 
  "projectName": "test-project",
  "description": "Node.js Application built with TypeScript",
  "keyConfigurationFiles": [
    {
      "name": "package.json",
      "location": "/root/test-project/package.json",
      "content": {
        "name": "test-project",
        "version": "1.0.0",
        "scripts": {
          "start": "ts-node src/index.ts"
        },
        "dependencies": {
          "@types/node": "^18.0.0",
          "jest": "^29.4.3",
          "typescript": "^4.7.4"
        }
      }
    },
    {
      "name": "tsconfig.json",
      "location": "/root/test-project/tsconfig.json",
      "content": {
        "compilerOptions": {
          "target": "es6",
          "module": "commonjs",
          "outDir": "dist",
          "strict": true
        }
      }
    },
    {
      "name": "jest.config.js",
      "location": "/root/test-project/jest.config.js",
      "content": {
        "moduleFileExtensions": ["ts", "tsx"],
        "testPathIgnorePatterns": ["/node_modules/"]
      }
    }
  ]
}
